// 修改图片地址
document.querySelector(".one-page-site_banner .icon-edit").onclick = () => {
  let newURL = window.prompt("请输入新的图片地址！");
  console.log(newURL);
  document.querySelector(".one-page-site_banner img").src = newURL;
};

css:
.one-page-site_banner {
  display: flex;
  height: 300px;
  background-color: rgba(0, 0, 0, 0.6);
  padding: 0 18px;
  justify-content: space-around;
  align-items: stretch;
}
.one-page-site_banner .img-box {
  height: 100%;
  max-width: 100%;
  position: relative;
}
.one-page-site_banner .img-box img {
  height: 100% !important;
  -webkit-user-select: none;
     -moz-user-select: none;
      -ms-user-select: none;
          user-select: none;
}
.one-page-site_banner .img-box .iconfont {
  position: absolute;
  visibility: hidden;
  top: 10px;
  right: 10px;
  font-size: 40px;
  cursor: pointer;
}
.one-page-site_banner .img-box .outline {
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  right: 0;
  border: 2px solid transparent;
}

body[data-preview-mark] .one-page-site_banner .img-box {
  height: 100%;
  max-width: 100%;
  position: relative;
}
body[data-preview-mark] .one-page-site_banner .img-box:hover .outline {
  border-color: black !important;
}
body[data-preview-mark] .one-page-site_banner .img-box:hover .iconfont {
  visibility: visible;
}/*# sourceMappingURL=index.css.map */

